mapper.insert:
INSERT INTO woorido.accounts (
    id, user_id, balance, locked_balance, bank_code, account_number, account_holder, version, created_at, updated_at
) VALUES (
    #{id}, #{userId}, #{balance}, #{lockedBalance}, #{bankCode, jdbcType=VARCHAR}, #{accountNumber, jdbcType=VARCHAR}, #{accountHolder, jdbcType=VARCHAR}, #{version}, SYSTIMESTAMP, SYSTIMESTAMP
)

mapper.select:
SELECT * FROM ( SELECT t.*, ROWNUM AS rn FROM ( SELECT at.id, at.account_id, at.type, at.amount, at.balance_before, at.balance_after, at.locked_before, at.locked_after, at.idempotency_key, at.related_challenge_id, at.related_user_id, at.description, at.pg_provider, at.pg_tx_id, at.created_at FROM woorido.account_transactions at WHERE at.account_id = #{accountId}
AND at.type = #{type}
AND at.created_at >= TO_TIMESTAMP(#{startDate} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS')
AND at.created_at <= TO_TIMESTAMP(#{endDate} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
ORDER BY at.created_at DESC ) t WHERE ROWNUM <= (#{page} + 1) * #{size} ) WHERE rn > #{page} * #{size}

SELECT COUNT(*)
FROM woorido.account_transactions at
WHERE at.account_id = #{accountId}

    AND at.type = #{type}


    AND at.created_at >= TO_TIMESTAMP(#{startDate} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS')


    AND at.created_at <= TO_TIMESTAMP(#{endDate} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')

SELECT NVL(SUM(CASE WHEN amount > 0 THEN amount ELSE 0 END), 0) AS totalIncome, NVL(SUM(CASE WHEN amount < 0 THEN ABS(amount) ELSE 0 END), 0) AS totalExpense FROM woorido.account_transactions at WHERE at.account_id = #{accountId}
AND at.type = #{type}
AND at.created_at >= TO_TIMESTAMP(#{startDate} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS')
AND at.created_at <= TO_TIMESTAMP(#{endDate} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')

SELECT COUNT(*)
FROM woorido.account_transactions
WHERE account_id = #{accountId}
  AND type = 'SUPPORT'
  AND related_challenge_id = #{challengeId}
  AND created_at >= TRUNC(SYSTIMESTAMP, 'MM')

SELECT COUNT(*)
FROM woorido.account_transactions
WHERE account_id = #{accountId}
  AND type = 'SUPPORT'
  AND related_challenge_id = #{challengeId}
